/*
 * $Id: sumcosts.c 1.1 2001/01/22 13:47:32 lefevre Exp lefevre $
 *
 * Sum the costs generated by qtree (or similar programs).
 *
 */

#include <stdio.h>
#include <stdlib.h>

int get_next(FILE *f)
{
  int ch;

  ch = getc(f);
  if (ch != EOF)
  {
    ch = getc(f);
    if (ch == EOF)
    {
      fclose(f);
      exit(0);
    }
  }
  return ch;
}

int main(int argc, char **argv)
{
  FILE *f;
  int m;
  long n;

  if (argc != 2)
  {
    fprintf(stderr, "Usage: sumcosts <file>\n");
    exit(1);
  }

  f = fopen(argv[1], "rb");
  if (f == NULL)
  {
    fprintf(stderr, "sumcosts: cannot open file!\n");
    exit(2);
  }

  get_next(f);

  m = 2;
  n = 1;
  while (1)
  {
    unsigned long s = 0;
    long i;

    for (i = n; i; i--)
      s += get_next(f);

    printf("m = %d, qav = %lu / 2^%d\n", m, s, m-2);
    m++;
    n <<= 1;
  }
}
